import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';

const ColorDisplay = (props) => {

    return (
        <div className="bg-white rounded" style={{ boxShadow: "0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)" }}>
            <div style={{ backgroundColor: props.color, maxHeight: props.height, minHeight: "20px", maxWidth: props.width }}>
            </div>
            {/* <div style={{ color: "#4A4A4A", fontWeight: "bold", paddingLeft: "5px"}}>
                Color
            </div> */}
            <div style={{ color: "hsl(0,0%,29%)", fontWeight: "400", paddingLeft: "5px"}}>
                {props.color}
            </div>
        </div>
    );
}

export default ColorDisplay;
