import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';
import { Box, Grid, Image, Text } from "@chakra-ui/core";

const ColorDisplay = (props) => {

    return (
        <Box width={[
            "15%",
            "25%",
            "50%",
            "100%"
        ]}
        className="bg-white rounded" 
        style={{ boxShadow: "0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)" }}>
            <Box width={[props.width, props.width, props.width]} height={[props.height, props.height, props.height]} style={{ backgroundColor: props.color }}

            >
            </Box>
            {/* <div style={{ color: "#4A4A4A", fontWeight: "bold", paddingLeft: "5px"}}>
                Color
            </div> */}
            <div style={{ color: "hsl(0,0%,29%)", fontWeight: "400", paddingLeft: "5px"}}>
                {props.color}
            </div>
        </Box>
    );
}

export default ColorDisplay;
