import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";
import { Box, Grid, Image, Text } from "@chakra-ui/core";
import ErrorCard from './ErrorCard';


const ColorCard = (props) => {

    const displayError = () => {
        if (!error.error) {
            return null;
        }
        return <Alert color="danger" isOpen={true} toggle={closeError}>{error.message}</Alert>;
    };

    return (
        <Box borderWidth="1px" rounded="lg" className="bg-white rounded shadow-longer">
                <Box bg="gray.100" color="gray.600" borderColor="gray.400" p="3" d="flex" style={{ alignItems: "center" }}>
                    <Image 
                    objectFit="cover"
                    src={props.profilePhoto} 
                    size={["75px","80px","90px"]}
                    rounded="full"
                    className="shadow-sharp"
                    />
                    <Box
                    color="gray.600"
                    fontWeight="bold"
                    letterSpacing="wide"
                    ml="2"
                    fontSize={["s","m","lg"]}
                    >
                       {props.name}
                    </Box>
                </Box>
                <Box p="3">
                    {/* <Grid style={{ border: "0px" }} templateColumns="repeat(3, 10fr)" gap={2}>
                            {props.palette && props.palette.map(color => (
                                    <Box style={{ border: "0px" }}><ColorDisplay color={color} height={150} width={150}/></Box>)
                                )
                            }
                    </Grid> */}
                    <ErrorCard/>
                </Box>
        </Box>
    );
}

export default ColorCard;
