import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";
import { Box, Grid, Image, Text } from "@chakra-ui/core";


const ColorCard = (props) => {

    return (
        <Box borderWidth="1px" rounded="lg" className="bg-white rounded" style={{ boxShadow: "0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)" }}>
                <Box bg="gray.50" color="gray.600" borderColor="gray.400" p="3" d="flex" style={{ alignItems: "center" }}>
                    <Image 
                    objectFit="cover"
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSgDJYmbILAP767hCDplTi2gkQDJwCSTWLrrIKeXIbcR9N5_A8L" 
                    size={["75px","80px","90px"]}
                    rounded="full"
                    style={{ boxShadow: "0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)" }}
                    />
                    <Box
                    color="gray.600"
                    fontWeight="semibold"
                    letterSpacing="wide"
                    ml="2"
                    fontSize={["s","m","lg"]}
                    >
                       {props.name}
                    </Box>
                </Box>
                <Box p="3">
                    <Grid style={{ border: "0px" }} templateColumns="repeat(3, 10fr)" gap={2}>
                            {props.palette.map(color => (
                                    <Box style={{ border: "0px" }}><ColorDisplay color={color} height={150} width={150}/></Box>)
                                )
                            }
                    </Grid>
                </Box>
        </Box>
    );
}

export default ColorCard;
