import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';

const ColorDisplay = (props) => {

    return (
        <div className="shadow-sm bg-white rounded">
            <div style={{ backgroundColor: props.color, height: props.height, width: props.width }}>
            </div>
            {/* <div style={{ color: "#4A4A4A", fontWeight: "bold", paddingLeft: "5px"}}>
                Color
            </div> */}
            <div style={{ color: "#4A4A4A", fontWeight: "400", paddingLeft: "5px"}}>
                {props.color}
            </div>
        </div>
    );
}

export default ColorDisplay;
