import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col,
    ListGroup,
    ListGroupItem,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Button
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";
import paletteService from "./../services/paletteService";

const FormUser = (props) => {

    const [input, setInput] = useState("");
    const [palette, setPalette] = useState([]);

    const save = e => {
        setInput(e.target.value);
    };

    const onSave = e => {
        var p = paletteService.getPalette(input);
        setPalette(p);
        console.log(p)
        e.preventDefault();
    }

    return (
        <div>
            <Container>
                <Row style={{display: 'flex', justifyContent: 'center'}}>
                    <Col md="6" sm="12">
                        <Form onSubmit={e => onSave(e)}>
                                <Input className="shadow border-0 rounded-0" type="text" name="username" id="username" placeholder="@Username..." onChange={e => setInput(e)}/>
                        </Form>
                    </Col>
                </Row>
                <Row style={{display: 'flex', justifyContent: 'center'}} className="top30">
                    <ListGroup style={{ border: "0px" }} className="list-group-horizontal-md">
                        {palette.map(color => (
                            <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={color} height={150} width={150}/></ListGroupItem>)
                        )
                        }
                    </ListGroup>
                </Row>
            </Container>
        </div>
    );
}

export default FormUser;
