import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';

const ColorDisplay = (props) => {

    return (
        <div className="shadow bg-white rounded">
            <div style={{ backgroundColor: props.color, height: "150px", width: "150px" }}>
            </div>
            <div style={{ color: "#373D3F", fontWeight: "light", paddingLeft: "5px"}}>
                {props.color}
            </div>
        </div>
    );
}

export default ColorDisplay;
