import React, { useState, useEffect } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col,
    ListGroup,
    ListGroupItem,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Button
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";
import Hooks from "./../services/hooks";

const FormUser = (props) => {

    const [value, setInput] = useState("");
    const [palette, setPalette] = useState([]);
    const [search, setSearch] = useState(false);

    const save = e => {
        setInput(e.target.value);
        console.log(value);
    };

    const onSave = async (e) => {
        setSearch(true);
        e.preventDefault();
    }

    useEffect(() => {
        const fetchData = async () => {
            const result = await paletteService.getPalette(value);
            setPalette(result);
            setSearch(false);
        };

        if (search) {
            fetchData();
        }
    }, [search]);

    return (
        <div>
            <Container>
                <Row style={{display: 'flex', justifyContent: 'center'}}>
                    <Col md="6" sm="12">
                        <Form onSubmit={e => onSave(e)}>
                                <Input className="shadow border-0 rounded-0" type="text" name="username" id="username" placeholder="@Username..." onChange={e => setInput(e.target.value)}/>
                        </Form>
                    </Col>
                </Row>
                <Row style={{display: 'flex', justifyContent: 'center'}} className="top30">
                    <ListGroup style={{ border: "0px" }} className="list-group-horizontal-md">
                        {palette.map(color => (
                            <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={color} height={150} width={150}/></ListGroupItem>)
                        )
                        }
                    </ListGroup>
                </Row>
            </Container>
        </div>
    );
}

export default FormUser;
