import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col,
    ListGroup,
    ListGroupItem
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";

const FormUser = (props) => {

    const [input, setInput] = useState("");

    const save = e => {
        setInput(e.target.value);
    };

    const onSave = e => {
        e.preventDefault();
    }

    return (
        <div>
            <Container style={{position: 'absolute', left: '50%', top: '50%',transform: 'translate(-50%, -50%)'}}>
                <Row style={{display: 'flex', justifyContent: 'center'}}>
                    <Col md="6">
                        <Form onSubmit={e => onSave(e)}>
                                <Input type="text" name="username" id="username" placeholder="@Username..." onChange={e => setInput(e)}/>
                        </Form>
                    </Col>
                </Row>
                <Row className="top30" style={{display: 'flex', justifyContent: 'center'}}>
                    <ListGroup style={{ border: "0px" }} className="list-group-horizontal">
                        <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"#302B2A"}/></ListGroupItem>
                        <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"#D3B383"}/></ListGroupItem>
                        <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"#8A6858"}/></ListGroupItem>
                        <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"#83A79D"}/></ListGroupItem>
                        <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"#7C8DA6"}/></ListGroupItem>
                    </ListGroup>
                </Row>
            </Container>
            <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
        </div>
    );
}

export default FormUser;
