import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";
import { Box, Grid, Image, Text } from "@chakra-ui/core";


const ColorCard = (props) => {

    return (
        <Box borderWidth="1px" rounded="lg" overflow="hidden" className="bg-white rounded" style={{ boxShadow: "0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)" }}>
                <Box d="flex" style={{ alignItems: "center", marginLeft: "10px", marginTop: "10px" }}>
                    <Image 
                    objectFit="cover"
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSgDJYmbILAP767hCDplTi2gkQDJwCSTWLrrIKeXIbcR9N5_A8L" 
                    size={["75px","80px","90px"]}
                    rounded="full"
                    />
                    <Box
                    color="gray.900"
                    fontWeight="semibold"
                    letterSpacing="wide"
                    ml="2"
                    fontSize={["s","m","lg"]}
                    >
                       {props.name}
                    </Box>
                </Box>
                <Box p="3">
                    <Grid style={{ border: "0px" }} templateColumns="repeat(3, 10fr)" gap={2}>
                            {props.palette.map(color => (
                                    <Box style={{ border: "0px" }}><ColorDisplay color={color} height={150} width={150}/></Box>)
                                )
                            }
                    </Grid>
                </Box>
        </Box>
    );
}

export default ColorCard;
