import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col,
    ListGroup,
    ListGroupItem
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";

const FormUser = (props) => {

    const [input, setInput] = useState("");

    const save = e => {
        setInput(e.target.value);
    };

    const onSave = e => {
        e.preventDefault();
    }

    return (
        <Container style={{position: 'absolute', left: '50%', top: '50%',transform: 'translate(-50%, -50%)'}}>
            <Row style={{display: 'flex', justifyContent: 'center'}}>
                <Col md="6">
                    <Form onSubmit={e => onSave(e)}>
                            <Input type="text" name="username" id="username" placeholder="@Username..." onChange={e => setInput(e)}/>
                    </Form>
                </Col>
            </Row>
            <Row className="top30" style={{display: 'flex', justifyContent: 'center'}}>
                <ListGroup style={{ border: "0px" }} className="list-group-horizontal">
                    <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"#302B2A"}/></ListGroupItem>
                    <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"rgb(211,166,131)"}/></ListGroupItem>
                    <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"rgb(138,104,88)"}/></ListGroupItem>
                    <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"rgb(131,167,157)"}/></ListGroupItem>
                    <ListGroupItem style={{ border: "0px" }}><ColorDisplay color={"rgb(124,141,166)"}/></ListGroupItem>
                </ListGroup>
            </Row>
        </Container>
    );
}

export default FormUser;
