import React, { useState } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Input,
    Row,
    Col
  } from 'reactstrap/';
import ColorDisplay from "./ColorDisplay";
import { Box, Grid } from "@chakra-ui/core";


const ColorCard = (props) => {

    return (
        <Box  maxW="sm" borderWidth="1px" rounded="lg" overflow="hidden" className="bg-white rounded" style={{ boxShadow: "0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)" }}>
            <Box p="3">
                <Box>
                    Hector de Luna
                </Box>
                <Grid style={{ border: "0px" }} templateColumns="repeat(3, 10fr)" gap={2}>
                        {props.palette.map(color => (
                                <Box style={{ border: "0px" }}><ColorDisplay color={color} height={150} width={150}/></Box>)
                            )
                        }
                </Grid>
            </Box>
        </Box>
    );
}

export default ColorCard;
