import React, { useState, useEffect } from 'react';
import {
    Container,
    Form,
    FormGroup,
    Label,
    Row,
    Col,
    ListGroup,
    ListGroupItem,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Button,
    Spinner,
    Fade,
    Alert
  } from 'reactstrap/';
import ColorCard from "./ColorCard";
import Hooks from "./../services/hooks";
import paletteService from "./../services/paletteService";
import { Box, Grid, Input, InputGroup, InputLeftElement, Icon } from "@chakra-ui/core";

const FormUser = (props) => {

    const [value, setInput] = useState("");
    const [palette, setPalette] = useState([]);
    const [search, setSearch] = useState(false);
    const [error, setError] = useState({ error: false, message: "" });

    const closeError = () => setError({ error: false, message: "" });

    const save = e => {
        setInput(e.target.value);
        console.log(value);
    };

    const onSave = async (e) => {
        setSearch(true);
        e.preventDefault();
    }

    useEffect(() => {
        const fetchData = async () => {
            const result = await paletteService.getPalette(value);

            if (result.length == 0) {
                setError({error: true, message: "Make sure the user is public and has photos! uWu"});
            } else {
                setPalette(result);
            }

            setSearch(false);
        };

        if (search) {
            setPalette([]);
            fetchData();
        }
    }, [search]);

    const displayLoading = () => {
        if (!search) {
            return null;
        }
        return <Spinner color="primary" size="lg"/>;
    };


    const displayError = () => {
        if (!error.error) {
            return null;
        }
        return <Alert color="danger" isOpen={true} toggle={closeError}>{error.message}</Alert>;
    };

    return (
        <div>
            <Container>
                <Row style={{display: 'flex', justifyContent: 'center'}}>
                    <Col md="6" sm="12">
                        <Form onSubmit={e => onSave(e)}>
                                <InputGroup>
                                <InputLeftElement children={<Icon name="search" color="gray.300" />} />
                                <Input type="text" name="username" id="username" placeholder="Username..." onChange={e => setInput(e.target.value)} style={{ boxShadow: "0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)" }}/>
                                </InputGroup>
                        </Form>
                    </Col>
                </Row>
                <Row style={{display: 'flex', justifyContent: 'center'}} className="top30">
                    { displayLoading() }
                    { displayError() }
                    <Fade in={!search} timeout={700}>
                        <ColorCard name={value} palette={palette}/>
                    </Fade>
                </Row>            
            </Container>
        </div>
    );
}

export default FormUser;
